class Solution {
public:
    string reorganizeString(string s) {
        map<char, int> mp;
        int maxi = 0;
        for (int i = 0; i < s.size(); i++) {
            mp[s[i]]++;
        }

        char c;
        for (auto i : mp) {
            if (i.second > maxi) {
                maxi = i.second;  // majority frequency
                c = i.first;   //  majority charactor 
            }
        }

        if (maxi > (s.size() + 1) / 2) {
            return "";  // Not possible to reorganize.
        }
