// OPTIMAL SOLUTION (using map only)
/*
TC : O(n)
SC : O(1)
*/
class Solution {
public:
    string reorganizeString(string s) {
        int n = s.length();

        // create map to store characters and their frequencies
        int map[26] = {0}; // SC: O(1)
        for(int i = 0; i < n; i++) { // TC: O(n)
            map[s[i]-'a']++;
        }

        int maxFreq = -1, maxFreqChar = '#';
        // finding 'maxFreqChar' and 'maxFreq'
        for(int i = 0; i < 26; i++) { // TC: O(1)
            if(map[i] > maxFreq) {
                maxFreq = map[i];
                maxFreqChar = i + 'a';
            }
        }

        // 
        if(maxFreq > (n+1)/2)
            return "";

        int index = 0;
        while(maxFreq > 0 && index < n) { // TC: O(n)
            s[index] = maxFreqChar;
            index += 2;
            maxFreq--;
        }

        map[maxFreqChar - 'a'] = 0;

        for(int i = 0; i < 26; i++) {
            while(map[i] > 0) { // TC: O(n)
                index = (index >= n) ? 1 : index;
                s[index] = i + 'a';
                index += 2;
                map[i]--;
            }
        }

        return s;
    }
};
