class Solution:
    def reorganizeString(self, s: str) -> str:
        t = []
        q = [(-cnt, c) for c, cnt in Counter(s).items()]; heapify(q)
        while 1 < len(q):
            cnt_a, a = heappop(q)
            cnt_b, b = heappop(q)
            t.append(a); cnt_a += 1
            t.append(b); cnt_b += 1
            if cnt_a: heappush(q, (cnt_a, a))
            if cnt_b: heappush(q, (cnt_b, b))
        if len(q):
            _, a = heappop(q)
            t.append(a)
        return ''.join(t) if len(s) == len(t) else ''
