            Map.Entry<Character, Integer> cur = q.poll();
            res.append(cur.getKey());
            cur.setValue(cur.getValue() - 1);
            if(last != null){
                q.offer(last);
            }
            last = cur.getValue() > 0 ? cur : null;

        }
        return res.length() == S.length() ? res.toString() : "";
    }
