class Solution {
public:
    string reorganizeString(string S) {
        unordered_map<char,int> pool;
        int cnt = 0;
        for(auto e : S)
        {
            pool[e]++;
            cnt = max(cnt, pool[e]);
        }
        if(cnt > (S.size()+1) / 2) return "";

        priority_queue<pair<int,char>>pq;
        for(auto e : pool)
            pq.push({e.second, e.first});

        int idx = 0;
        while(!pq.empty())
        {
            auto e = pq.top();
            pq.pop();
            while(e.first > 0)
            {
                e.first--;
                S[idx] = e.second;
                idx += 2;
                if(idx >= S.size()) idx = 1;
            }
        }

        return S;

    }
};

