        Pair prev = new Pair('#', -1);

        StringBuilder sb = new StringBuilder();

        while(!pq.isEmpty()){
            Pair curr = pq.poll();
            sb.append(curr.ch);
            curr.freq--;

            if(prev.freq > 0){
                pq.add(prev);
            }

            prev =curr;
        }

        if(sb.length() != s.length()) return "";
        return sb.toString();


    }
}




















