class Solution(object):
    def reorganizeString(self, s):
        d = Counter(s)
        if max(d.values()) > ceil(len(s)/2):
            return ''
        res = [' ']*len(s)
        c=d.most_common()
        i=0
        for e in c:
            for j in range(e[1]):
                res[i]=e[0]
                i+=2
                if i>=len(s):
                    i=1
        return ''.join(res)
