string reorganizeString(string s) {
        //create a map to get the count of the characters in the string
        unordered_map<char,int> mp;
        for(int i=0;i<s.size();i++){
            mp[s[i]]++;
        }

        //create a max heap having the pair with characters sorted by the count
        priority_queue<pair<int,char>> q;
        for(auto [k,v] : mp){
            q.push({v,k});
        }

        string ans="";
        //loop until queue is empty
        while(!q.empty()){
            pair<int,char> x=q.top();
            q.pop();
            pair<int,char> y;

            //if the queue is empty and the last character in the answer string is not the same 
            //as the one we have in x, append it and return it.
            if(q.empty() && (ans.size()==0 || ans[ans.size()-1]!=x.second)){
                ans+=x.second;
                x.first-=1;
                return (x.first>0?"":ans);
            }
            //if the queue is empty and the last element is same as x, then simply return ""
            else if(q.empty()){
                return "";
            }
            //else set value of y
            else{
                y=q.top();
                q.pop();
            }
            if(ans.size()==0 || ans[ans.size()-1]!=x.second){
                ans+=x.second;
                ans+=y.second;
            }
            else{
                ans+=y.second;
                ans+=x.second;
            }

            //reduce the count in x and y and push it back to the queue if their value is greater than 0
            x.first-=1;
            y.first-=1;
            if(x.first)
                q.push(x);
            if(y.first)
                q.push(y);
        }

        return ans;
