	public String reorganizeString(String S) {

		if(S==null || S.isEmpty() ){
			return S;
		}
		int n = S.length();
		StringBuilder newStr = new StringBuilder();

	   // Create map for character frequency
		Map<Character,Integer> chCount = new HashMap<>();
		for (char ch : S.toCharArray()){
			chCount.put(ch, chCount.getOrDefault(ch,0)+1);
		}

        // log k which 26 
		PriorityQueue<Map.Entry<Character,Integer>> maxHeap = new PriorityQueue<>((ent1,ent2)->{
			if(ent1.getValue().equals(ent2.getValue())) {
				return ent1.getKey().compareTo(ent2.getKey());
			}
			return ent2.getValue().compareTo(ent1.getValue());
		});

        // add all elements into MaxHeap
		maxHeap.addAll(chCount.entrySet());

        // clear the map 
        chCount.clear();

        Queue<Map.Entry<Character,Integer>> coolingQueue = new LinkedList<>();

        // O(n)  -- n log(k)
        while(!maxHeap.isEmpty()){
            Map.Entry<Character,Integer> topElement = maxHeap.poll();
            topElement.setValue(topElement.getValue()-1);
            newStr.append(topElement.getKey());
            coolingQueue.add(topElement);

            // Until Queue reaches K( 2) move on . Here replace 2 with K 
            if(coolingQueue.size() < 2){
                continue;
            }

            while(!coolingQueue.isEmpty()){
                 Map.Entry<Character,Integer> coolingElement = coolingQueue.poll();
                 if(coolingElement.getValue() > 0){
                    maxHeap.add(coolingElement); 
                 }

            }
        }
        return newStr.length() == S.length() ? newStr.toString() : "";
