from heapq import *
from collections import Counter
class Solution:
    def reorganizeString(self, s: str) -> str:        
        d = Counter(s)
        res = []
        h = [(-d[x], x) for x in d]
        heapify(h)
        while h:
            if len(h) > 1:
                v1, e1 = heappop(h)
                v2, e2 = heappop(h)
                res.extend([e1, e2])
                if v1 + 1 < 0:
                    heappush(h, (v1 + 1, e1))
                if v2 + 1 < 0:
                    heappush(h, (v2 + 1, e2))
            else:
                v, e = heappop(h)
                if res and res[-1] != e and v == -1:
                    res.append(e)
                else:
                    return ""
        return ''.join(res)
