class Solution:
    def reorganizeString(self, s: str) -> str:
        x=[]
        res=s[0]
        for i in range(1,len(s)):
            if x and x[-1]!=res[-1]:
                #Add to result which are non similar adjacent elements
                res=res+x.pop()
            if res[-1]!=s[i]:
                res=res+s[i]
            else:
                #Append the non similar adjacent elements to the x
                x.append(s[i])
