            if x[-1]!=res[0]:
                res=x.pop()+res
            if x and x[-1]!=res[-1]:
                res=res+x.pop()
            i=0
            while i<len(res)-1:
                #Simply add the elements to the result which are present in the x
                if x and x[-1]!=res[i] and x[-1]!=res[i+1]:
                    res=res[:i+1]+x.pop()+res[i+1:]
                i=i+1
        if len(res)!=len(s):
            return ""
        #Return the result
        return res

