class Solution {
public:
    string reorganizeString(string s) {
        int arr[26] = {0};
        int maxi = 0, i = 0;
        int n = s.size();

        for(auto it : s){
            if(++arr[it - 'a'] > arr[maxi])
                maxi = it - 'a';  // finding the index of most occuring character
        }

        if(2*arr[maxi] - n > 1)
            return "";

        while(arr[maxi]){  // putting the maximum occuring character at alternate position
            s[i] = 'a' + maxi;
            i += 2;
            arr[maxi]--;
        }

        for(int j = 0; j < 26; j++){  // putting all the remaining character at alternate position
            while(arr[j]){
                if(i >= n)
                    i = 1;
                s[i] = 'a' + j;
                arr[j]--;
                i += 2;
            }
        }
        return s;
    }
};
```

