class Solution {
public:
    string reorganizeString(string s) {
        unordered_map<char, int> mp;
        int n = s.size();
        for(auto it : s)
            mp[it]++;

        int maxi = 0;
        for(auto it : mp)
            maxi = max(maxi, it.second);

        if(maxi - (n - maxi) > 1)
            return "";

        priority_queue<pair<int, char>> pq;
        for(auto it : mp)
            pq.push({it.second, it.first});

        string ans = "";
        while(pq.size() > 1){
            auto a = pq.top();
            pq.pop();
            auto b = pq.top();
            pq.pop();

            ans += a.second;
            ans += b.second; 
            a.first--;
            b.first--;
            if(a.first)
                pq.push(a);
            if(b.first)
                pq.push(b);
        }

        if(!pq.empty() && pq.top().first > 1)
            return "";
        else if(!pq.empty() && pq.top().first == 1)
            ans += pq.top().second;
        return ans;

    }
};
