class Solution {
public:
    string reorganizeString(string s) {
        unordered_map<char,int> freq (26); // hashmap to count frequencys

        for(char c : s) freq[c]++;

        priority_queue<pair<int,char>> pq;  // max heap

        for(auto& [c, freq] : freq) pq.push({freq, c}); // inset [frequency ,character] pairs


        string out = "";

        // loop while there is still more characters left to add
        while(!pq.empty()) {
            // if the back character and the highest freqncy characte rare not the same
            if(out == "" || pq.top().second != out.back()) {     
                pair<int,char> largest = pq.top();
                pq.pop();

                out += largest.second;
                largest.first--;

                if(largest.first) pq.push(largest);

            } else {
                // store the largest so can access the second largest
                pair<int,char> largest = pq.top();
                pq.pop();

                if(pq.empty()) return ""; // if there is not a second largest character

                // add second largest character to string
                pair<int,char> second_largest = pq.top();
                pq.pop();

                out += second_largest.second;
                second_largest.first--;

                if(second_largest.first > 0) pq.push(second_largest);

                // can always add largest back since no update to its frequency
                pq.push(largest); 
            }
        }
        return out.size() == s.size() ? out : "";
    }
};
