def reorganizeString(self, S: str) -> str:
    hash = [0] * 26
    for char in S:
        hash[ord(char) - ord('a')] += 1

    max_count, letter = 0, 0
    for i in range(len(hash)):
        if hash[i] > max_count:
            max_count = hash[i]
            letter = i

    if max_count > (len(S) + 1) // 2:
        return ""

    res = [None] * len(S)
    idx = 0
    while hash[letter] > 0:
        res[idx] = chr(letter + ord('a'))
        idx += 2
        hash[letter] -= 1

    for i in range(len(hash)):
        while hash[i] > 0:
            if idx >= len(res):
                idx = 1
            res[idx] = chr(i + ord('a'))
            idx += 2
            hash[i] -= 1

    return ''.join(res)

class Solution {
public:
    string reorganizeString(string s) {
        priority_queue<pair<int,char>> pq;
map<char,int> mp;
string ans="";
pair<int ,char> block{0,'0'},temp{0,'0'};

for(char ch:s) mp[ch]++;

    for(auto p:mp) pq.push({p.second,p.first});
     block=pq.top();
     ans+=block.second;
     block.first--;
     pq.pop();
     while(pq.size()>0|| block!=NULL){
       if(!pq.size() && block!=NULL) return "";
       temp=pq.top();
       pq.pop();
       ans+=temp.second;
       temp.first--;
       if(block.first>0){
       pq.push(block);
       }
       block=temp;
     }



    return ans; 
    }
};

