class Solution:
    def combine(self, n: int, k: int) -> List[List[int]]:

        def countBit1s(num, n):
            bits = 0
            for i in range(n):
                if (num >> i) & 1:
                    bits += 1
            return bits

        ans = []
        for mask in range(1 << n):
            if countBit1s(mask, n) != k: continue
            sub = []
            for i in range(n):
                if (mask >> i) & 1:
                    sub.append(i+1)
            ans.append(sub)
        return ans
