def combine(self, n: int, k: int) -> List[List[int]]:

	# -- helper
	def recurse(start, combo):
		if len(combo) == k:
			res.append(combo[::]) # take a copy of combo at leaf before you pop/backtrack
		else:
			for i in range(start, n+1):
				combo.append(i) # maintain one combo variable for entire space tree
				recurse(i+1, combo)
				combo.pop() # - bactrack to move horiontally to sibling node
			return res
	# main
	res = []
	return recurse(1, [])
