    private void Helper(int n, int k, int i, List<int> l)
    {
        if (l.Count == k)
            res.Add(new List<int>(l));
        else
            for (int j = i; j <= n; j++)
            {
                l.Add(j);

                Helper(n, k, j + 1, l);

                l.RemoveAt(l.Count - 1);
