class Solution:
    def combine(self, n: int, k: int) -> List[List[int]]:
        result = []
        curr_combination = []
        self.backtrack(1, n, k, curr_combination, result)
        return result

    def backtrack(self, start, n, k, curr_combination, result):
        if len(curr_combination) == k:
            result.append(curr_combination[:])
            return
        for i in range(start, n + 1):
            curr_combination.append(i)
            self.backtrack(i + 1, n, k, curr_combination, result)
            curr_combination.pop()
