class Solution {
public:
    vector<string> generateParenthesis(int n) {
        string s;
        Generate(0,0,s,n);
        return ans;
    }
private:
    vector<string>ans;
    void Generate(int left,int right,string &s,int n)
    {
        if(left==n&&right==n) // base case: when left and right parentheses are equal to n.
        {
            ans.push_back(s);
            return;
        }

        // case I: if left parenteses are less than n, we can add a ( to the string. 
		//But we might also add a ) so we backtrack by poppping it.
        if(left<n)
		{
            s+='(';
            Generate(left+1,right,s,n);
            s.pop_back(); 
        }

        if(left>right) //case II: follows from I.
        {
            s+=')';
            Generate(left,right+1,s,n);
            s.pop_back();
        } 
    }
};
```

