class Solution {
public:
    vector<vector<string>> partition(string s) {
        n=s.size();
        Generate(s,0);
        return ans;   
    }
private:
    vector<vector<string>>ans;
    vector<string>Partition;
    int n;

    bool is_pal(string &s,int start,int end)
    {
        while(start<=end)
        {
            if(s[start++]!=s[end--])
                return false;
        }
        return true;
    }

    void Generate(string &s,int start)
    {
        if(start==n)
        {
            ans.push_back(Partition);
            return;
        }

        for(int i=start;i<n;i++)
        {
            if(is_pal(s,start,i))
            {
                Partition.push_back(s.substr(start,i-start+1));
                Generate(s,i+1);
                Partition.pop_back();
            }
        }
    } 
};
```

