        vector<vector<int>> ans;

        permutations(arr,0,ans);

        return ans;
    }
    void permutations(vector<int>&arr,int s,vector<vector<int>>&ans)
    {
        if(s==arr.size())
        {
            ans.push_back(arr);
            return;
        }
        for(int i=s;i<arr.size();i++)
        {
            swap(arr[s],arr[i]);
            permutations(arr,s+1,ans);
            swap(arr[s],arr[i]);
        }
    }
};
