function findCombination(n, k, position, seq, mutRes) {
    if (k === 0) { mutRes.push([...seq]); return; } 
    if (position > n) return;

    seq.push(position);
    findCombination(n, k - 1, position + 1, seq, mutRes);
    seq.pop();

    findCombination(n, k, position + 1, seq, mutRes);
}


