class Solution {
    func combine(_ n: Int, _ k: Int) -> [[Int]] {

        var res = [[Int]]()
        var data = [Int](repeating: 0, count: k)
        var i = 0

        while data[0] <= (n - k + 1) {

            data[i] += 1
            if data[i] > n {
                i -= 1
            } else if i == (k - 1) {
                res.append(data)
            } else {
                i += 1
                data[i] = data[i - 1]
            }
        }

        return res
    }
}
