public:
    vector<vector<int>> combine(int n, int k) {

        vector<vector<int>> aResult;
        vector<int> aData(k, 0);

        int i = 0;

        while (aData[0] <= (n - k + 1)) {

            aData[i]++;
            if (aData[i] > n) {
                i -= 1;
            } else if (i == (k - 1)) {
                aResult.push_back(aData);
            } else {
                i += 1;
                aData[i] = aData[i - 1];
            }
        }

        return aResult;

    }
};
