class Solution {
public:
    vector<vector<int> > combine(int n, int k) {
        int cur=0;
        vector<int> v(k,0);
        vector<vector<int> > ans;
        while (cur>=0) {
            if (cur==k) {
                ans.push_back(v);
                --cur;
                continue;
            }
            int val=v[cur]+1;
            if (cur>0) {
                val=max(val,v[cur-1]+1);
            }
            if (val<=n) {
                v[cur++]=val;
            } else {
                v[cur--]=0;
            }
        }
        return ans;
    }
};

