class Solution {
public:
    void backtrack(int ind, int n, int k, vector<int>&ds, vector<vector<int>>&res) {
         if(ds.size() == k) {
             res.emplace_back(ds);
             return;
         }
        for(int i=ind; i<=n; i++) {
            ds.emplace_back(i);
            backtrack(i+1, n, k, ds, res);
            ds.pop_back();
        }
    }
    vector<vector<int>> combine(int n, int k) {
        vector<vector<int>> res;
        vector<int> ds;
        backtrack(1, n, k, ds, res);
        return res;
    }
};
