class Solution:
    def combine(self, n: int, k: int) -> List[List[int]]:
        def backtrack(ind, n, k, ds, res):
            if len(ds) == k:
                res.append(ds[:])
                return
            for i in range(ind, n+1):
                ds.append(i)
                backtrack(i+1, n, k, ds, res)
                ds.pop()

        res = []
        backtrack(1, n, k, [], res)
        return res
