    public void combinations(int n , int k , List<List<Integer>> ans , List<Integer> temp){
        if(k == 0)
        {
            ans.add(new ArrayList(temp));
            return;
        }
        if(n == 0)
            return;

//         We are not taking n
        combinations(n-1, k, ans,temp);

//         We are taking n
        temp.add(n);
        combinations(n-1,k-1,ans,temp);
        temp.remove(temp.size()-1);
    }
}
