		//base case
        if(i>n || k==0){
            if(k==0)
                ans.push_back(v);
            return;
        }

		//pick up the element
        v.push_back(nums[i]);
        fn(i+1,k-1,n,nums,v,ans);
        v.pop_back();

		//dont pick up the element
        fn(i+1,k,n,nums,v,ans);
