```javascript
// recursive
var climbStairs = function(n) {
   if (n <= 2) return n;
    return climbStairs(n - 2) + climbStairs(n - 1);
};

// recursive top down (memoization)
const climbStairs = (n) => {
    const memo = new Map();

    return climbStairsMemo(n, memo);
};

const climbStairsMemo = (n, memo) => {
    if(n <= 2) return n;
    if(!memo.has(n)) {
        memo.set(n, climbStairsMemo(n - 1, memo) + climbStairsMemo(n - 2, memo))
    }

    return memo.get(n);
};

// iterative bottom-up
const climbStairs = (n) => {
    const memo = new Array(n + 1).fill(0);
    memo[0] = 0;
    memo[1] = 1;
    memo[2] = 2;
    for(let i = 3; i <= n; i++) {
        memo[i] = memo[i -1] + memo[i - 2];
    }

    return memo[n];
};

// iterative space-saver
const climbStairs = (n) => {
    if(n <= 2) return n;

    let prev2 = 1;
    let prev1 = 2;
    let curr = 0;
    for(let i = 3; i <= n; i++) {
        curr = prev2 + prev1;
        prev2 = prev1;
        prev1 = curr;
    }

    return curr;
}
