var climbStairs = function(n) {

    // create array of length n+1
    var memo = new Array(n+1);

    // Initialization on base case
    memo[0] = 1;
    memo[1] = 1;

    var climb = function( i ){

        if( i <= 1 ){
            // base case
            return memo[i];

        }else if( memo[i] != undefined ) {
            // climb(i) has been computed before, directly look-up memo
            return memo[i];
        }
        else{
            // general cases:
            memo[i] = (climb(i-1) + climb(i-2));
            return memo[i];
        }
    }

    return climb(n);
