    // for optimal - (O(1)) ( as we used two variables only)

  // approach 1

  int recursion(int n)
     { 
         // when n becomes 0 simply it means we reached the destination and when the n becomes -ve it means we reached beyond the target

       // make a array to store the count 
         if(n == 0)
         {
             return 1;
         }
         if( n < 0)
         {
             return 0;
         }
         int ans1 = recursion(n-1);
         int ans2 = recursion(n-2);
         return (ans1 + ans2);

     }

// approach 2

int topdown(int n , vector<int>& dp)
