class Solution {
public:
    int rand10() {
        //We use two calls to rand(7) for 49 posssible values w/ equal chance of occuring

        //Example values: "11" "12" "21" "77"
        //Then, we can form 10 groups of 4 values for our rand10().

        //40 values of 49 are used as possible values for our rand generator

        //If we generate one of the remaining 9 values, call rand10() again
        int firstRandomVal = rand7(); //Can be 1,2,3,4,5,6,7
        int secondRandomVal = rand7(); //Can be 1,2,3,4,5,6,7
        int appendedVals = stoi(to_string(firstRandomVal) + to_string(secondRandomVal)); //11 12 13 14 15 16 17, 21 22 23 24 25 26 27, etc ...

        if(appendedVals >= 11 && appendedVals <= 14) {
            return 1;
        } else if(appendedVals >= 15 && appendedVals <= 21) {
            return 2;
        } else if(appendedVals >= 22 && appendedVals <= 25) {
            return 3;
        } else if(appendedVals >= 26 && appendedVals <= 32) {
            return 4;
