Note that the second minimum value > minimum value, i.e. that the two mins must be distinct
> **T/S:** O(n)/O(n), where n = numer of nodes in the tree
```
public int findSecondMinimumValue(TreeNode root) {
	if (root == null)
		return -1;
	var secondMin = new int[]{-1};
	preorder(root, new int[]{root.val}, secondMin);
	return secondMin[0];
}

private void preorder(TreeNode root, int[] min, int[] secondMin) {
	if (root == null)
		return;
	// This is the first initialization of second min value of the tree. Do this if:
	// 1. the second min hasn't been initialized yet and this node's value != the minimum value in the tree, or
	// 2. this node's val is smaller than the current second min val
	if (root.val != min[0] && secondMin[0] == -1 || 
		min[0] < root.val && root.val < secondMin[0]) {

		secondMin[0] = root.val;
	} else if (root.val == min[0]) {
		// we need to traverse in this case because node might have a greater child or sub-child which could replace
		// second minimum
		preorder(root.left, min, secondMin);
		preorder(root.right, min, secondMin);
	}
	// if node.val > second minimum, we don't need to traverse further because this node and all the nodes in its
	// subtree will be greater than or equal to second minimum, so we can't find anything less than second minimum
}
