# Code
```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int findSecondMinimumValue(TreeNode* root);

    void inorderTraversal(TreeNode *node, set<int>& s);
};
/**********************************************************/
int Solution::findSecondMinimumValue(TreeNode* root) {
    set<int> s;
    set<int>::iterator it;
    if (root != nullptr) {
        inorderTraversal(root, s);
    }
    if (s.size() >= 2) {
        it = s.begin();
        ++it;
        return *it;
    }
    return -1;
}
/**********************************************************/
void Solution::inorderTraversal(TreeNode *node, set<int>& s) {
    if (node->left != nullptr) {
        inorderTraversal(node->left, s);
    }
    s.insert(node->val);
    if (node->right != nullptr) {
        inorderTraversal(node->right, s);
    }
    return;
}
/**********************************************************/
