class Solution {
public:
    int firstSmallest(TreeNode* root){
        queue<TreeNode*> q;
        q.push(root);
        int smallest = root->val;
        while(!q.empty()){
            TreeNode * temp = q.front();
            q.pop();
            if(temp->left){
                smallest = min (smallest, temp->left->val);
                q.push(temp->left);
            }
            if(temp->right){
                smallest = min (smallest, temp->right->val);
                q.push(temp->right);
            }
        }
        return smallest;
    }

    int secondSmallest(TreeNode* root, int smallest){
        queue<TreeNode*> q;
        q.push(root);
        int secondSmallest = INT_MAX;
        bool found = false;
        while(!q.empty()){
            TreeNode * temp = q.front();
            q.pop();
            if(temp->left){
                if(temp->left->val>smallest and temp->left->val <= secondSmallest) secondSmallest = temp->left->val, found = true;;
                q.push(temp->left);
            }
            if(temp->right){
                if(temp->right->val > smallest and temp->right->val <= secondSmallest) secondSmallest = temp->right->val, found = true;;
                q.push(temp->right);
            }
        }
        if(!found) return -1;
        return secondSmallest;
    }

    int findSecondMinimumValue(TreeNode* root) {
        int smallest = firstSmallest(root);
        return secondSmallest(root, smallest);
    }
};
