        if(pq.size() <=1) return -1;
        else{
            pq.poll();
            return pq.poll();
        }
    }
    public void inOrder(TreeNode root){
        if(root == null) return;

        inOrder(root.left);
        if(!pq.contains(root.val)) pq.add(root.val);
        inOrder(root.right);
    }
