class Solution {
public:
    int min, second_smallest = -1;

    inline void update_second_smallest(int new_val) {
        //assert(new_val >= min);
        if (new_val != min && (second_smallest == -1 || new_val < second_smallest))
            second_smallest = new_val;
        return ;
    }

    void recursive(TreeNode * root) {
        while (true) {
            update_second_smallest(root->val);
            if (!root->left)
                return ;
            if (root->left->val > root->right->val)
                swap(root->left, root->right);
            //assert(root->left->val == root->val);
            //Since root->right->val >= 0, the if () will not be called when second_smallest == -1
            int right_val = root->right->val;
            if (min < right_val && right_val < second_smallest)
                second_smallest = right_val;
            else
                recursive(root->right);
            root = root->left;
        }
        return ; //Unreachable
    }

    int findSecondMinimumValue(TreeNode* root) {
        min = root->val; //root->val is the minimum of the entire tree
        recursive(root);
        return second_smallest;
    }
};
