class Solution {
public:
//     another logic could be to do inorder traversal and make a set and return the 2nd value in it using next if set element count >=2;(eliminating call for duplicates).
    int findSecondMinimumValue(TreeNode* root) {
        if(!root->left)return -1;
        // no leaf nodes
        int v1,v2;
        if(root->val==max(root->left->val,root->right->val)){ //case1-left=right =root
            v1=findSecondMinimumValue(root->left);
            v2=findSecondMinimumValue(root->right);
            if(v1!=-1&&v2!=-1)return min(v1,v2);
            else return max(v1,v2); //case when both left and right do not possess any min 

        }
        else if(root->val==root->left->val){  //case2--as root= left <right 
            v1=root->right->val;
            v2=findSecondMinimumValue(root->left);
            if(v2!=-1)return min(v1,v2);
            return v1;
        }
        else if(root->val==root->right->val){  // case3--
            // root=min,root= right ..> left>root==right
            v1=root->left->val;
            v2=findSecondMinimumValue(root->right);
             if(v2!=-1)return min(v1,v2);
             return v1;
        }
        else{ // 4th case root < left<right or root< right<left 
            return min(root->left->val,root->right->val);
        }
    }
};
