    set<int> bag;

    void kthSmall(TreeNode* root, int k) {
        if ( root == nullptr ) { return; }
        kthSmall(root->left, k);

        if ( bag.count(root->val) == 0 ) { 
            bag.insert(root->val);
            if ( size(bag) == k ) { return; }
        }

        kthSmall(root->right, k);
    }

