        }

        kthSmall(root->right, k);
    }

    int findSecondMinimumValue(TreeNode* root) {
        kthSmall(root, 2);
        if ( size(bag) < 2 ) { return -1; }
        return vector<int>(bag.begin(), bag.end()).at(1);
    }
};
