class Solution {
public:
    void inorder(TreeNode* root,vector<int> &v)
    {
        if(root==NULL)
        return;
        inorder(root->left,v);
        v.push_back(root->val);
        inorder(root->right,v);
    }
    int findSecondMinimumValue(TreeNode* root) {
        vector<int> v;
        inorder(root,v);
        sort(v.begin(),v.end());
        int i=0;
        while(i<v.size()&&v[i]==v[0])
        i++;
        if(i==v.size())
        return -1;

        return v[i];
    }
};
```
