class Solution {
public:
    int minVal(TreeNode* root,int rootVal)
    {
        if(!root)
        return -1;
        if(root->val!=rootVal)
        return root->val;

        int left=minVal(root->left,rootVal),right=minVal(root->right,rootVal);
        if(left==-1)
        return right;
        if(right==-1)
        return left;
        return min(left,right);
    }
    int findSecondMinimumValue(TreeNode* root) {
    if(root==NULL)
    return -1;
        int ans=minVal(root,root->val);
        return ans;
    }
};
```
