class Solution {
    # ordered set will store the elements in sorted order
    set<int>s;

    # this function is to iterate through every node and store the value in the set
    void traverse(TreeNode* root)
    {
        s.insert(root->val);
        # using this no base condition is required in recursion
        if(root->left)
        traverse(root->left);
        if(root->right)
        traverse(root->right);
    }
public:
    int findSecondMinimumValue(TreeNode* root) {
        int k=1;
        traverse(root);
        # You can use `auto` in for loop instead of iterator declaration
        set<int>::iterator i;
        for(i=s.begin();i!=s.end();i++)
        {
            # the condition to get the second minimum element 
            # and break out of the loop
            if(!k)
            {
                return *i;
            }
            k--;
        }
        # if second element doesn't exist then return -1
        return -1;
    }
};
```

