class Solution {
public:
    int numJewelsInStones(string jewels, string stones) {
        int s1 = jewels.size();
        int s2 = stones.size();

        vector<int> hash(256, 0);
        int ans = 0;

        for(int i=0; i<s1; i++) {
            hash[jewels[i]]++;
