class Solution {
public:
    int dp[59][58];

    int helper(int n, int idx)
    {
       if(n == 0 or idx == 0) return 1;

       if(dp[n][idx] != -1) return dp[n][idx];

       if(idx > n) return dp[n][idx] = helper(n, idx - 1);

       return dp[n][idx] = max((idx * helper(n - idx, idx)), helper(n , idx - 1));
    }

    int integerBreak(int n)
    {
        for(int i = 0; i < n + 1; i++)
        {
            for(int j = 0; j < n; j++)
                dp[i][j] = -1;
        }

        return helper(n, n - 1);
    }
};
```

--> bottom up approach(0 ms)
```
class Solution {
public:

    int integerBreak(int n)
    {
        int dp[n + 1][n];

        for(int row = 0; row < n + 1; row++)
        {
            for(int col = 0; col < n; col++)
            {
                if(row == 0 || col == 0)
                 dp[row][col] = 1;
            }
        }

        for(int row = 1; row < n + 1; row++)
        {
            for(int col = 1; col < n; col++)
            {
                if(row >= col)
                  dp[row][col] = max(col * dp[row - col][col], dp[row][col - 1]);

                else
                    dp[row][col] = dp[row][col - 1];
            }
        }

        return dp[n][n - 1];      
    }
};
```

