class Solution {
public: 

    int dp[60][60]; 

    int integerBreak(int n) { 

        vector<int> nums; 

        for(int i = 1; i <= n/2; i++) { 
            nums.push_back(i);
        } 

        if (n % 2 != 0) { 
            nums.push_back((n/2) + 1);
        }

        int s = nums.size(); 

        int dp[s+1][n+1]; 

        for (int i = 0; i <= s; i++) { 

            for (int j = 0; j <= n; j++) {  

                if (i == 0) { 
                    dp[i][j] = 0;
                }  

                else if (j == 0) { 
                    dp[i][j] = 1;
                }

                else if (nums[i-1] <= j) { 
                    dp[i][j] = max(nums[i-1] * dp[i][j-nums[i-1]], dp[i-1][j]);
                } 

                else { 
                    dp[i][j] = dp[i-1][j];
                }
            }
        }

        return dp[s][n];
    }
};
