public class Solution {
    public int integerBreak(int n) {
        return solve_magic(n);
    }

    // accepted
    private int solve_topdown(int n, int[] dp) {
        if (n == 2) {
            dp[2] = 1;
            return dp[2];
        }

        if (dp[n] > 0) {
            return dp[n];
        }

        for(int i = n - 1; i >=2; i--) {
            // do not break
            dp[n] = Math.max(dp[n], i * (n - i));

            // do break
            dp[n] = Math.max(dp[n], solve_topdown(i, dp) * (n - i));
        }

        return dp[n];
    }

    // accepted
    private int solve_bottomup(int n) {
        int[] dp = new int[n + 1];
        dp[2] = 1;
        for(int i = 3; i <= n; i++) {
           for(int j = 2; j <= i; j++) {
               // do not break
               dp[i] = Math.max(dp[i], j * (i - j));

               // do break
               dp[i] = Math.max(dp[i], dp[j] * (i - j));
           }
        }

        return dp[n];
    }

    // accepted
    private int solve_magic(int n) {
        switch(n) {
            case 2: return 1;
            case 3: return 2;
            case 4: return 4;
            case 5: return 6;
            case 6: return 9;
            default: return solve_magic(n - 3) * 3;
        }
    }
}

